sources.scss/components/_testimonials.scss:
.testimonials {

  .container {

    h2 {

    }

    .wrap {

      @media (min-width: 992px) {
        display: flex;
        flex-wrap: wrap;
        justify-content: space-around;
        gap: 20px;
      }

      blockquote {
        background-color: $secondary;
        color: $primary;
        padding: 2em;
        padding-bottom: 1em;
        border-radius: 5px;
        position: relative;
        margin-top: 2em;
        max-width: 275px;
        margin-left: auto;
        margin-right: auto;

        @media (min-width: 500px) {
          max-width: 350px;
        }

        @media (min-width: 992px) {

        }

        &::before {
          font-family: Georgia, serif;
          position: absolute;
          font-size: 3.7em;
          line-height: 1;
          top: 0.2em;
          left: 0.2em;
          content: "\201C";

          @media (min-width: 992px) {
            left: 0.5em;
          }
        }

        &::after {
          font-family: Georgia, serif;
          position: absolute;
          font-size: 3.7em;
          float: right;
          line-height: 1;
          right: 0.2em;
          bottom: -0.2em;
          content: "\201D";

          @media (min-width: 500px) {
            right: 0.5em;
          }

          @media (min-width: 992px) {
            right: 0.8em;
            top: 1.8em;
          }
        }


        p {
          @media (min-width: 600px) {
            margin-right: -10px;
          }
        }

        cite {
          display: block;
          margin-top: 0.7em;
          margin-right: 0.5em;

          &::before {
            content: '\2014';
            margin-right: 0.5em;
          }
        }
      }
    }
  }
}

sources.scss/base/_type.scss:
@import url('https://fonts.googleapis.com/css2?family=EB+Garamond&family=Oswald&display=swap');

$min_width: 320px;
$max_width: 1200px;
$min_font: 15px;
$max_font: 18px;

html {
  font-family: 'EB Garamond', serif;
  @include fluid-type($min_width, $max_width, $min_font, $max_font);
}

body {
  line-height: 1.4;
  letter-spacing: 0.75px;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  font-family: 'Oswald', sans-serif;
  line-height: 1.275;
}

$mod_1: 1.2; // mobile
$mod_2: 1.55; // desktop

h1, .h1 {  
  font-size: $mod_1*$mod_1*$mod_1*$mod_1 *1rem; 
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1*$mod_1*$mod_1 *$min_font, $mod_2*$mod_2*$mod_2*$mod_2/1.2 *$min_font);
}

h2, .h2 {  
  font-size: $mod_1*$mod_1*$mod_1 *1rem; 
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1*$mod_1 *$min_font, $mod_2*$mod_2*$mod_2/1.2 *$min_font);
}

h3, .h3 { 
  font-size: $mod_1*$mod_1 *1rem;
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1 *$min_font, $mod_2*$mod_2 *$min_font);
}

h4, .h4 {
  font-size: $mod_1 *1rem;
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1 *$min_font, $mod_2 *$min_font);
}

h5, .h5 {
  font-size: $mod_1 *1rem;
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1 *$min_font, $mod_2 *$min_font);
}

h6, .h6 {
  font-size: $mod_1 *1rem;
  @include fluid-type($min_width, $max_width, $mod_1*$mod_1 *$min_font, $mod_2 *$min_font);
}

.highlight {
  color: $tertiary;
}

sources.scss/base/_normalize.scss:
html {
  box-sizing: border-box;
  font-size: 16px;
  width: 100%;
  height: 100%;
}

*,
*:before,
*:after {
  box-sizing: inherit;
}

body {
  background-color: $primary;
  color: $secondary;
  text-align: center;
}

body,
h1,
h2,
h3,
h4,
h5,
h6,
p,
ol,
ul,
figure,
blockquote {
  margin: 0;
  padding: 0;
  font-weight: normal;
}

ol,
ul {
  list-style: none;
}

img {
  width: 100%;
  height: auto;
}

a {
  text-decoration: none;
  color: $secondary;

  &:hover {
    color: $tertiary;
  }
}

button,
input,
textarea {
  font-size: inherit;
}

sources.scss/base/_mixins.scss:
@function strip-unit($value) {
  @return $value / ($value * 0 + 1);
}

@mixin fluid-type($min-vw, $max-vw, $min-font-size, $max-font-size) {
  $u1: unit($min-vw);
  $u2: unit($max-vw);
  $u3: unit($min-font-size);
  $u4: unit($max-font-size);

  @if $u1 == $u2 and $u1 == $u3 and $u1 == $u4 {
    & {
      font-size: $min-font-size;
      @media screen and (min-width: $min-vw) {
        font-size: calc(#{$min-font-size} + #{strip-unit($max-font-size - $min-font-size)} * ((100vw - #{$min-vw}) / #{strip-unit($max-vw - $min-vw)}));
      }
      @media screen and (min-width: $max-vw) {
        font-size: $max-font-size;
      }
    }
  }
}

