      }

      .slide-outer {

      }

      .btn-wrap {
        margin-top: 1.6em;
      }
    }
  }

  .testimonials {

    @media (min-width: 992px) {
      margin-top: 7em;
    }

    .container {

      h2 {
        margin-bottom: 1.5em;

        @media (min-width: 992px) {
          margin-bottom: 0.5em;
        }
      }
    }
  }

  .books-scroll {

    .container {

      @media (min-width: 992px) {
        position: relative;
        width: 100%;
        max-width: 100%;
        min-height: 100vh;
      }

      .scroll-wrap {

        @media (min-width: 992px) {
          width: 100%;
          position: relative;
        }

        .sticky {

          @media (min-width: 992px) {
            position: sticky;
            top: 0;
            height: 100vh;
            width: 100%;
            overflow-x: hidden;
          }

          .scroll-stage {

            @media (min-width: 992px) {
              position: absolute;
              height: 100%;
              will-change: transform;
              overflow: hidden;
              display: flex;
              align-items: center;
              overflow-x: hidden;
            }

            .cards {

              @media (min-width: 992px) {
                position: relative;
                margin-top: 0;
                height: auto;
                display: flex;
                flex-flow: row nowrap;
                justify-content: flex-start;
                align-items: flex-start;

                @media (min-width: 992px) {
                  padding-left: calc((100vw - 850px) / 2);
                }

                @media (min-width: 1200px) {
                  padding-left: calc((100vw - 1050px) / 2);
                }
              }

              article {
                margin: 0 auto;

                @media (min-width: 992px) {
                  width: 750px;
                  flex-shrink: 0;
                  position: relative;
                  display: flex;
                  align-items: center;
                  justify-content: space-between;
                }

                &:not(:last-of-type) {
                  margin-bottom: 6em;

                  @media (min-width: 992px) {
                    margin-bottom: 0;
                    margin-right: 225px;
                  }
                }

                figure {

                  a {

                    img {
                      width: 45%;
                      max-width: 270px;

                      @media (min-width: 992px) {
                        width: 100%;
                        max-width: 250px;
                      }
                    }

                    figcaption {
                      display: none;
                    }
                  }
                }

                .wrap {

                  @media (min-width: 992px) {
                    margin-left: 50px;
                  }

                  h4 {
                    margin: 1em 0;
                  }

                  p {
                    text-align: left;
                    margin: 0 auto;
                    line-height: 1.6;
                    width: 90%;
                    max-width: 400px;

                    @media (min-width: 992px) {
                      max-width: 450px;
                      text-align: center;
                    }
                  }

                  a.btn {
                    margin-top: 1em;
                    position: relative;
                  }
                }
              }
            }
          }
        }
      }
    }
  }

  .cta {

    .container {

      h3 {

      }

      a {
        margin: 2em 0;
      }

      figure {

        img {
          margin-top: 1em;
          max-width: 900px;
        }

        figcaption {
          display: none;
        }
      }
    }
  }
}

sources.scss/pages/_books.scss:
main#books {

  .timeline {

    .container {

      h1 {
      }

      .wrap {
        margin-top: 1em;

        article {
          position: relative;

          &:not(:first-of-type) {
            padding-top: 80px;

            @media (min-width: 992px) {
              padding-top: 0;
            }
          }

          @media (min-width: 992px) {
            display: flex;
            justify-content: space-between;
            align-items: center;
            height: 650px;

            &:nth-of-type(odd) {
              flex-direction: row-reverse;

              div {
                text-align: left;
              }
            }

            &:nth-of-type(even) {

              div {
                text-align: right;
              }
            }
          }

          figure {

            img {
              max-width: 220px;

              @media (min-width: 992px) {
                max-width: 250px;
              }
            }

            figcaption {
              display: none;
            }
          }

          .timeline-line {
            display: none;
            height: 100%;
            width: 12px;
            background-color: $secondary;
            position: absolute;
            left: 50%;
            transform: translateX(-50%);

            @media (min-width: 992px) {
              display: block;
            }

            svg {
              position: absolute;
              top: 50%;
              left: 50%;
              transform: translate(-50%, -50%);
            }

            &.top {
              top: 50%;
              height: 50%;

              svg {
                top: 0;
              }
            }

            &.bottom {
              top: 0;
              height: 50%;

              svg {
                top: 100%;
              }
            }
          }

          div {

            h2 {
              padding-top: 0.5em;
            }

            p {
              max-width: 500px;
              margin-left: auto;
              margin-right: auto;
              margin-top: 1.5em;

              @media(min-width: 992px) {
                max-width: 300px;
              }
            }

            a {
              margin-top: 2.5em;
            }
          }
        }
      }
    }
  }
}

sources.scss/pages/_book.scss:
main#book {

  section.content {
    margin-top: 4em;

    @media (min-width: 768px) {
      margin-top: 8em;
    }

    .container {
      @media (min-width: 450px) {
        width: 80%;
        margin-left: auto;
        margin-right: auto;
      }

      @media (min-width: 768px) {
        max-width: 750px;
      }

      p:first-of-type {
        margin-top: 1em;
        position: relative;
        text-indent: 1.5em;
      }

      p {
        text-align: left;
        margin: 2em 0;
        line-height: 1.55;

        .dropcap {
          color: $tertiary;
          font-size: 4em;
          text-indent: 30px;
          line-height: 0;
          margin-right: 0.05em;
        }
      }

      .art {
        position: relative;

        picture {

          img {

          }
        }

        span {
          position: absolute;
          top: 0;
          left: 0;
          font-size: 2.5em;
          font-family: 'Oswald', sans-serif;
          line-height: 1;
        }

        &.jamais {
          display: flex;
          justify-content: flex-end;
          background-color: #000;

          picture {

            img {
              max-width: 300px;
            }
          }

          span {
            bottom: 0;
            top: initial;

            @media (min-width: 850px) {
              bottom: 40%;
              left: 10px;
              text-align: left;
              width: 450px;
            }
          }
        }

        &.secrets {
          background-color: #000;
          display: flex;
          justify-content: flex-end;
          max-height: 400px;


          picture {

            img {
              object-fit: cover;
              height: 100%;
              object-position: 0% 60%;
              max-width: 600px;
              height: 100%;
            }
          }

          span {
            top: 10px;
            width: 250px;
            left: 50%;
            transform: translateX(-50%);

            @media (min-width: 790px) {
              left: 50px;
              top: 50px;
              transform: none;
            }
          }
        }

        &.escape {
          background-color: #000;

          picture {

            img {
              max-width: 350px;
            }
          }

          span {
            top: 30px;
            right: 0;
            left: 0;

            @media (min-width: 768px) {
              right: initial;
              top: 20%;
              left: 30px;
            }
          }
        }

        &.sweet-home {
          background-color: #000;

          picture {

            img {
              max-width: 350px
            }
          }

          span {
            top: 50px;
            right: 0;
            left: 0;
            max-width: 400px;
            margin-left: auto;
            margin-right: auto;

            @media (min-width: 600px) {
              bottom: 30px;
              top: initial;
              left: 30px;
              margin: 0;
            }
          }
        }

        &.watcher {
          background-color: #000;
          display: flex;
          justify-content: flex-end;

          picture {

            img {
              max-width: 450px;
            }
          }

          span {
            top: 30px;

            @media (min-width: 375px) {
              max-width: 350px;
              left: 0;
              top: 50px;
            }
            @media (min-width: 768px) {
              max-width: 350px;
              left: 30px;
              top: initial;
              bottom: 50px;
              transform: translateY(-50%);
            }
          }
        }
      }
    }
  }

  section.more {

    .container {

      h3 {

      }

      .wrap {
        display: flex;
        justify-content: space-between;
        margin-top: 1em;
        max-width: 1000px;
        margin-left: auto;
        margin-right: auto;

        @media (min-width: 768px) {
          margin-top: 2em;
        }

        a {
          width: 23%;

          img {
            max-width: 200px;
          }
        }
      }
    }
  }
}

sources.scss/pages/_about.scss:
#about {

  .intro {
    margin-top: 2em;

    @media(min-width: 600px) {
      margin-top: 4.5em;
    }

    @media (min-width: 992px) {
      margin-top: 9em;
    }

    .container {

      .wrap {
        align-items: flex-end;

        figure {

          img {

          }

          figcaption {

          }
        }

        .wrap-col {

          h1 {

            @media(min-width: 600px) {
              margin-top: 0.5em;
            }
          }

          h2.h4 {

          }
        }
      }
    }
  }

  .content {

    @media (min-width: 768px) {
      margin-top: 7em;
    }

    .container {
      max-width: 600px;

      @media (min-width: 1200px) {
        max-width: 800px;
      }

      p {
        text-align: left;
        margin: 2em 0;
        line-height: 1.55;
        margin-left: auto;
        margin-right: auto;

        .dropcap {
          color: $tertiary;
          font-size: 4em;
          text-indent: 30px;
          line-height: 0;
          margin-right: 0.05em;
        }

        &.last {
          text-align: center;
          font-size: 1.3em;
          letter-spacing: 1.5px;
        }
      }

      .grid {
        display: grid;
        grid-gap: 20px;
        min-height: 590px;

        @media (min-width: 480px) {
          grid-template-columns: repeat(2, 1fr);
          grid-template-rows: repeat(2, 1fr);
        }

        img {
          width: 100%;
          height: 100%;
          object-fit: cover;

          @media (min-width: 480px) {

            &:nth-child(1) {

            }

            &:nth-child(2) {
              grid-row-start: 1;
              grid-row-end: 3;
              grid-column-start: 2;
            }

            &:nth-child(3) {

            }
          }
        }
      }

      .grid2 {
        display: grid;
        grid-gap: 20px;

        @media (min-width: 480px) {
          grid-template-columns: repeat(2, 1fr);

        }

        img {
          width: 100%;
          height: 100%;
          object-fit: cover;

          &:not(:first-of-type) {
            margin-top: 2.5em;

            @media (min-width: 480px) {
              margin-top: 0;

              &:nth-child(1) {

              }

              &:nth-child(2) {

              }

              &:nth-child(3) {
                grid-column-end: span 2;
                height: 350px;
                object-position: 100% 70%;
              }
            }

            @media (min-width: 992px) {
              &:nth-child(3) {
                height: 450px;
              }
            }
          }
        }
      }
    }
  }
}

sources.scss/pages/_contact.scss:
main#contact {

  .intro {

    .container {

      h1 {

      }

      p {
        margin: 1em auto;
        max-width: 500px;

        @media (min-width: 992px) {
          margin-top: 2em;
        }
      }

      .social-wrap {
        display: flex;
        justify-content: space-between;
        max-width: 300px;
        margin: 0 auto;
        margin-top: 3em;

        img {
          width: 60px;
          cursor: pointer;
        }
      }
    }
  }

  .contact-form {
    margin-bottom: 6em;

    .container {

      form {
        width: 100%;
        max-width: 600px;
        margin: 0 auto;

        .form-style {
          border: 2px solid $secondary;
          background-color: $primary;
          color: $secondary;
          font-family: inherit;
          padding: 0.4em;
          border-radius: 3px;
        }

        .wrap {
          display: flex;
          flex-wrap: wrap;
          justify-content: space-between;

          .wrap-50 {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            flex-basis: 100%;

            @media (min-width: 600px) {
              flex-basis: 48%;
            }

            label {
              margin-bottom: 0.5em;
            }

            input {
              width: 100%;
              margin-bottom: 1em;
            }
          }

          textarea {
            width: 100%;
            height: 250px;

            @media (min-width: 992px) {
              height: 350px;
            }
          }

          button {
            width: 100%;
            margin-top: 1em;
          }
        }
      }
    }
  }
}
