.modal-outer {
  position: fixed;
  height: 100vh;
  width: 100vw;
  z-index: 105;
  background-color: rgba(0, 0, 0, 0.7);
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  opacity: 0;
  pointer-events: none;
  transition: all 0.5s ease;

  .modal-inner {
    background-color: $primary;
    padding: 3em;
