      .grid2 {
        display: grid;
        grid-gap: 20px;

        @media (min-width: 480px) {
          grid-template-columns: repeat(2, 1fr);

        }

        img {
          width: 100%;
          height: 100%;
          object-fit: cover;

          &:not(:first-of-type) {
            margin-top: 2.5em;

            @media (min-width: 480px) {
              margin-top: 0;

              &:nth-child(1) {

              }

              &:nth-child(2) {

              }

              &:nth-child(3) {
                grid-column-end: span 2;
                height: 350px;
                object-position: 100% 70%;
              }
            }

            @media (min-width: 992px) {
              &:nth-child(3) {
                height: 450px;
              }
            }
          }
        }
      }
    }
  }
}

sources.scss/pages/_contact.scss:
main#contact {

  .intro {

    .container {

      h1 {

      }

      p {
        margin: 1em auto;
        max-width: 500px;

        @media (min-width: 992px) {
          margin-top: 2em;
        }
      }

      .social-wrap {
        display: flex;
        justify-content: space-between;
        max-width: 300px;
        margin: 0 auto;
        margin-top: 3em;

        img {
          width: 60px;
          cursor: pointer;
        }
      }
    }
  }

  .contact-form {
    margin-bottom: 6em;

    .container {

      form {
        width: 100%;
        max-width: 600px;
        margin: 0 auto;

        .form-style {
          border: 2px solid $secondary;
          background-color: $primary;
          color: $secondary;
          font-family: inherit;
          padding: 0.4em;
          border-radius: 3px;
        }

        .wrap {
          display: flex;
          flex-wrap: wrap;
          justify-content: space-between;

          .wrap-50 {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            flex-basis: 100%;

            @media (min-width: 600px) {
              flex-basis: 48%;
            }

            label {
              margin-bottom: 0.5em;
            }

            input {
              width: 100%;
              margin-bottom: 1em;
            }
          }

          textarea {
            width: 100%;
            height: 250px;

            @media (min-width: 992px) {
              height: 350px;
            }
          }

          button {
            width: 100%;
            margin-top: 1em;
          }
        }
      }
    }
  }
}
